	function openSearch() {
		if($(".max").is(":visible")) {
			$(".max").hide();
			$(".search").show();
		}
	}
	function closeSearch() {
		if($(".max").is(":hidden")) {
			$(".max").show();
			$(".search").hide();
		}
	}
</script>
<script>
	var h = document.documentElement.clientHeight;
	var w = document.documentElement.clientWidth;
	var b = w - $(".fdl").width();
	var a = h - $(".header").height();
	$(".fdl").height(a);
//	$(".fdl").css({
//			"maxHeight": a
//		})
		$(".ejfl_li").height(a);
//	$(".ejfl").css({
//		"maxHeight": a
//	})
	$(".ejfl").width(b-10);
	$(".fdbox").width(w);
	$(".max").height(h);
	$(".fdl li").on("click", $(".fdl li a"), function() {
		$(".fdl li a").removeClass("syfl_activate");
		$(this).find("a").addClass("syfl_activate");
	})
</script>
<script type="text/javascript">
	function select_ej(id) {
		$.ajax({
			type: "get",
			url: "/ecshop_v3.0.0_utf8_release0530/mobile/index.php?m=default&c=category&a=all",
			async: true,
			data: "id=" + id + "&ajax=ajax",
			dataType: "Json",
			success: function(result, status) {
				if(status) {
					$(".ejfl_li li").remove();
				 for(var o in result){   
					$(".ejfl_li").append("<li><a href='"+result[o]['url']+"'>"+result[o]['name']+"</a></li>")
			      }  
				}
				
			}
		});
	}
</script>